SELECT
  c_18_00052,
  c_17_00021,
  108.56495101346042-0.3630508488702458*c_18_00052 AS prediction_21,
  (c_17_00021-108.56495101346042+0.3630508488702458*c_18_00052)*
  (c_17_00021-108.56495101346042+0.3630508488702458*c_18_00052) loss
FROM
   t_50999999
WHERE
 c_datetime >= TIMESTAMP('2014-07-21 10:00:00');
